DROP TABLE IF EXISTS `tb_article`;
CREATE TABLE `tb_article` (
  `article_id` int(11) NOT NULL AUTO_INCREMENT,
  `article_title` varchar(50) NOT NULL DEFAULT '',
  `article_tag` varchar(50) DEFAULT NULL,
  `article_author_id` int(11) NOT NULL DEFAULT '0',
  `article_text` text,
  `article_image_title` varchar(500) DEFAULT NULL,
  `article_pageviews` int(11) DEFAULT '0',
  `article_likecount` int(11) NOT NULL DEFAULT '0',
  `article_comment` int(11) NOT NULL DEFAULT '0',
  `article_heat` int(11) NOT NULL DEFAULT '0',
  `created` datetime DEFAULT NULL,
  `updated` datetime DEFAULT NULL,
  PRIMARY KEY (`article_id`)
) ENGINE=InnoDB AUTO_INCREMENT=10032 DEFAULT CHARSET=utf8;

#
# Data for table "tb_article"
